auth_url = globals.auth_url;
realm = globals.realm;
clientid = globals.clientid;
userid = globals.userid;
password = globals.password;
client_secret = globals.client_secret;

const echoPostRequest = {
  url: auth_url + '/auth/realms/' + realm + '/protocol/openid-connect/token',
  method: 'POST',
  header: 'Content-Type:application/x-www-form-urlencoded',
  body: {
    mode: 'raw',
    raw: 'grant_type=password&client_id=' + clientid 
          + '&username=' + userid 
          + '&password=' + password
          + '&client_secret=' + client_secret
  }
};
pm.sendRequest(echoPostRequest, function (err, res) {
    var jsonData = res.json();
    if (jsonData.hasOwnProperty('access_token')) {
    	pm.globals.set("token", jsonData.access_token);
	    pm.globals.set("refresh_token", jsonData.refresh_token);
	    console.log(err ? err : res.json());
	} else {
	    pm.globals.set("token", 0);
	    pm.globals.set("refresh_token", 0);
	    pm.globals.set("token_expires", 0);
	    pm.globals.set("refresh_token_expires", 0);
	}
});

//  If no maximum response defined, set a default.
response_max = 0;
if (globals.response_max) {
    response_max = JSON.parse(globals.response_max);
}
else {
    response_max = 5009;
    pm.globals.set("response_max", JSON.stringify(response_max));
};

//  Get the max response time allowed.
pm.test('Response time less than ' + response_max.toString() + 'ms', function(){
    pm.expect(pm.response.responseTime).to.be.below(response_max);
});

pm.test("Response code for request is 200", function(){
    pm.response.to.have.status(200);
});
pm.test('Response header should have Content-Type of application/json', function() {
    pm.response.to.have.header('content-type', 'application/json');
});
pm.test('Response body be in JSON format', function() {
    pm.response.to.be.json; 
});

