//  If no maximum response defined, set a default.
response_max = 0;
if (globals.response_max) {
    response_max = JSON.parse(globals.response_max);
}
else {
    response_max = 5009;
    pm.globals.set("response_max", JSON.stringify(response_max));
};

//  Get the max response time allowed.
pm.test('Response time less than ' + response_max.toString() + 'ms', function(){
    pm.expect(pm.response.responseTime).to.be.below(response_max);
});

pm.test("Response code for request is 200", function(){
    pm.response.to.have.status(200);
});
pm.test('Response header should have Content-Type of application/json', function() {
    pm.response.to.have.header('content-type', 'application/json');
});
pm.test('Response body be in JSON format', function() {
    pm.response.to.be.json; 
});
