//  Function returns a weighted randomized exam type index.
function get_random_index(exam_array) {

    //  Generate a random number up to the maximum weight allowed.
    random_number = Math.floor(Math.random() * exam_array[exam_array.length - 1].weight);

    //  Get the index of the exam type corresponding to that weight.
    index = get_index(random_number, exam_array);

    //  Return the index.
    return index;
}

//  Based on a random number, turns it into a weighted randomized exam type index.
function get_index(random_value, exam_array) {
    index = 0;
    var i;
    for (i = 0; i < exam_array.length; i++) {
        if (random_value <= exam_array[i].weight) {
            index = i;
            break;
        }
    }

    return index;
}

