auth_url = globals.auth_url;
realm = globals.realm;
clientid = globals.clientid;
userid = globals.userid;
password = globals.password;
client_secret = globals.client_secret;

const echoPostRequest = {
  url: auth_url + '/auth/realms/' + realm + '/protocol/openid-connect/token',
  method: 'POST',
  header: 'Content-Type:application/x-www-form-urlencoded',
  body: {
    mode: 'raw',
    raw: 'grant_type=password&client_id=' + clientid 
          + '&username=' + userid 
          + '&password=' + password
          + '&client_secret=' + client_secret
  }
};
pm.sendRequest(echoPostRequest, function (err, res) {
    var jsonData = res.json();
    if (jsonData.hasOwnProperty('access_token')) {
    	pm.globals.set("token", jsonData.access_token);
	    pm.globals.set("refresh_token", jsonData.refresh_token);
	    if (err) {
	        console.log(err);
	    }
	    // console.log(err ? err : res.json());
	} else {
	    pm.globals.set("token", 0);
	    pm.globals.set("refresh_token", 0);
	    pm.globals.set("token_expires", 0);
	    pm.globals.set("refresh_token_expires", 0);
	}
});

