//  Finds an exam name in the JSON list allTypes of exam types.
function find_exam(input_exam_name, allTypes) {
	exam_id_value = -1;

    //  Loop to look for the input exam name.
    if (allTypes) {
        allTypes.forEach(function(type) {
            if ((type.exam_type_name) == input_exam_name) {
            exam_id_value = type.exam_type_id;
            }
        });
    }

    //  Return the exam_id_type of the input exam name.
    return exam_id_value;
}

